import React, {Component} from 'react';
import {
  Button,
  Divider,
  Grid,
  Header,
  Icon,
  Container,
  Segment,
} from 'semantic-ui-react';

class LandingPage extends Component {
  handleAdmin = () => {
    this.props.history.push({pathname: '/admin'});
  };
  handleAspirants = () => {
    this.props.history.push({pathname: '/registerAspirants'});
  };
  handleVoters = () => {
    this.props.history.push({pathname: '/Register'});
  };
  handleVoting = () => {
    this.props.history.push({pathname: '/election'});
  };

  render() {
    return (
      <div>
        <h1>E-voting Application</h1>
        <br />
        <br />
        <br />
        <br />

        <Segment placeholder color="white">
          <Grid columns={4} stackable textAlign="center">
            {/* <Container> */}
            <Grid.Row verticalAlign="middle">
              <Grid.Column>
                <Header icon>
                  <Icon name="user circle" />
                  Admin access only
                </Header>
                <Button color="red" onClick={this.handleAdmin}>
                  Admin
                </Button>
              </Grid.Column>
              <Divider vertical />
              <Grid.Column>
                <Header icon>
                  <Icon name="registered" />
                  Register to contest
                </Header>
                <Button color="yellow" onClick={this.handleAspirants}>
                  Register Aspirants
                </Button>
              </Grid.Column>
              <Divider vertical />
              <Grid.Column>
                <Header icon>
                  <Icon name="users" />
                  Register to Vote
                </Header>
                <Button color="blue" onClick={this.handleVoters}>
                  Register Voters
                </Button>
              </Grid.Column>
              <Divider vertical />
              <Grid.Column>
                <Header icon>
                  <Icon name="erase" />
                  Vote for candidate
                </Header>
                <Button color="green" onClick={this.handleVoting}>
                  Election
                </Button>
              </Grid.Column>
            </Grid.Row>
            {/* </Container> */}
          </Grid>
        </Segment>
      </div>
    );
  }
}

export default LandingPage;
