import React, {Component} from 'react';
import {Image, Segment, Button, Container} from 'semantic-ui-react';
import firebaseConf from './Firebase'

class Result extends Component {
  constructor() {
    super();
    this.state = {
      contestantsPost: []
    };
  }
  componentDidMount(){
    this.fetchAspirantsdata()
  }
  fetchAspirantsdata = () => {
    firebaseConf
      .database()
      .ref('posts')
      .orderByChild('post')
      .on('value', snapshot => {
        const contestantsPost = [];
        snapshot.forEach(data => {
          contestantsPost.push({
            id: data.val().id,
            post: data.val().post,
            aspirants: Object.entries(
              data.val().aspirants ? data.val().aspirants : []
            ).map(e => Object.assign(e[1], {aspirantKey: e[0]})),
          });
          this.setState({contestantsPost});
          console.log(this.state.contestantsPost);
        });
      });
  };
  render() {
    const {contestantsPost}=this.state
    return (
      <div>
        <Container>
        
          {contestantsPost.map((item)=>{
            return(<Segment.Group>
            <Segment>Top</Segment>
            <Segment.Group>
              <Segment>Nested Top</Segment>
              <Segment>Nested Middle</Segment>
              <Segment>Nested Bottom</Segment>
            </Segment.Group>
            <Segment>Bottom</Segment>
          </Segment.Group>)
          }
          )}
        </Container>
      </div>
    );
  }
}

export default Result;
