import React ,{Component} from 'react'
import {Form,Input,Button} from 'semantic-ui-react'

class AddId extends Component{
    constructor(){
        super()
        this.state={
            fullName:'',
            studentId:''
        }
    }
    handleChange = e => {
        this.setState({[e.target.name]: e.target.value});
      };
    render(){
        const{fullName,studentId}=this.state
        return(
            <div>
                <marquee>The field must be filled with CAPITAL LETTERS</marquee>
            <Form onChange={this.handleChange}>
            <Form.Group widths="equal">
            <Form.Field
              control={Input}
              label="Fullname"
              placeholder="Fullname"
              name="fullName"
              value={fullName}
            />
            <Form.Field
              control={Input}
              label="Student ID"
              placeholder="Student ID"
              name="studentId"
              value={studentId}
            />
           
          </Form.Group>
          <Form.Field
            control={Button}
            type="submit"
            color="blue"
            disabled={
                !fullName ||
                !studentId }>
            Submit
            </Form.Field>
          </Form>
          </div>
        )
    }
}

export default AddId
