import React, {Component} from 'react';
import {Form, Input, Button} from 'semantic-ui-react';
import firebaseConf from './Firebase';

class AddId extends Component {
  constructor() {
    super();
    this.state = {
      fullName: '',
      schoolId: '',
      isLoading:false
    };
  }
  handleChange = e => {
    this.setState({[e.target.name]: e.target.value});
  };

  handleSubmit = () => {
      this.setState({isLoading:true})
    const idRef = firebaseConf.database().ref(`schoolInfo`);
    let childId = this.state.schoolId;
    idRef.child(childId).set({
      fullname: this.state.fullName,
      schoolId: this.state.schoolId,
    }).then(
        this.setState({isLoading:false})
    );
  };

  render() {
    const {fullName, schoolId} = this.state;
    return (
      <div>
        <marquee>The field must be filled with CAPITAL LETTERS</marquee>
        <Form onChange={this.handleChange} onSubmit={this.handleSubmit}>
          <Form.Group widths="equal">
            <Form.Field
              control={Input}
              label="Fullname"
              placeholder="Fullname"
              name="fullName"
              value={fullName}
            />
            <Form.Field
              control={Input}
              label="Student ID"
              placeholder="Student ID"
              name="schoolId"
              value={schoolId}
            />
          </Form.Group>
          <Form.Field
            control={Button}
            type="submit"
            color="blue"
            disabled={!fullName || !schoolId}
            loading={this.state.isLoading}
          >
            Submit
          </Form.Field>
        </Form>
      </div>
    );
  }
}

export default AddId;
